
            //accessing JQUERY  (JavaScript function that wraps around everything)
$("document").ready(function() {

                //computer selects random number, to be matched, between range 19 - 120  
    function randomLarge() {
        var R = Math.floor(Math.random() * 120) + 1;    
        return R;
    }

                //random number generated by the function then stored in the variable computerRandomSel
    var computerRandomSel = randomLarge();      

                //random number added to webpage as text       
    $("#computerRandom").text(computerRandomSel);     

                //random numbers selected for the crystals, between range 1 - 12 
    function crystalsRandomSel() {
        var C = Math.floor(Math.random() * 12) + 1;
        return C;
    }


                //storing the random numbers
    var Ruby = crystalsRandomSel();                       
    var Diamond = crystalsRandomSel();
    var Topaz = crystalsRandomSel();
    var Emerald = crystalsRandomSel();

    var totalScore = 0;
    var wins = 0;
    var losses = 0;


                //adding scores from clicking on four cystals
    $("#ruby").on("click", function() {
        totalScore = totalScore + Ruby;
        $("#totalPoints").text(totalScore);
        scoreCheck(computerRandomSel);
    })

    $("#diamond").on("click", function() {
        totalScore = totalScore + Diamond;
        $("#totalPoints").text(totalScore);
        scoreCheck(computerRandomSel);
    })

    $("#topaz").on("click", function() {
        totalScore = totalScore + Topaz;
        $("#totalPoints").text(totalScore);
        scoreCheck(computerRandomSel);
    })

    $("#emerald").on("click", function() {
        totalScore = totalScore + Emerald;
        $("#totalPoints").text(totalScore);
        scoreCheck(computerRandomSel);
    })


                // Game Add points-Win-Loss-Reset
    function scoreCheck(computerR) {
        if (totalScore < computerR) {
            $("#totalPoints").text(totalScore);
        }
        else if (totalScore === computerR) {
            wins = wins + 1;
            $("#wins").text("Wins: " + wins);
            totalScore = 0;
            $("#totalPoints").text(totalScore);
            computerRandomSel = randomLarge();
            $("#computerRandom").text(computerRandomSel);
            Ruby = crystalsRandomSel();
            Diamond = crystalsRandomSel();
            Topaz = crystalsRandomSel();
            Emerald = crystalsRandomSel();

        }
        else if (totalScore > computerR) {
            losses = losses +1;
            $("#losses").text("Losses: " + losses);
            totalScore = 0;
            $("#totalPoints").text(totalScore);
            computerRandomSel = randomLarge();
            $("#computerRandom").text(computerRandomSel);
            Ruby = crystalsRandomSel();
            Diamond = crystalsRandomSel();
            Topaz = crystalsRandomSel();
            Emerald = crystalsRandomSel();
        }  
    }
}) 
